import "./App.css";
import { useState } from "react";
import React from "react";

type Note = {
  id: number;
  title: string;
  content: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    // Create Dummy Notes
    {
      id: 1,
      title: "Test note 1",
      content: "note content placeholder",
    },
    {
      id: 2,
      title: "Test note 2 ",
      content: "note content placeholder",
    },
    {
      id: 3,
      title: "Test note 3",
      content: "note content placeholder",
    },
    {
      id: 4,
      title: "Test note 4 ",
      content: "note content placeholder",
    },
    {
      id: 5,
      title: "Test note 5",
      content: "note content placeholder",
    },
    {
      id: 6,
      title: "Test note 6",
      content: "note content placeholder",
    },
    {
      id: 7,
      title: "Test note 7",
      content: "note content placeholder",
    },
    {
      id: 8,
      title: "Test note 8",
      content: "note content placeholder",
    },
  ]);

  // State for Form Inputs
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  // Track selected note
  const [selectedNote, setSelectedNote] = useState<Note | null>(null);

  // Handle Clicking on a note
  const handleNoteClick = (note: Note) => {
    setSelectedNote(note);
    setTitle(note.title);
    setContent(note.content);
  };

  // Handle Form Submission
  const handleAddNote = (event: React.FormEvent) => {
    // Prevent form from submitting and refreshing page
    event.preventDefault();

    // Create a New Note Object
    const newNote: Note = {
      id: notes.length + 1,
      title: title,
      content: content,
    };

    // Update State with New Note
    setNotes([newNote, ...notes]);

    // Reset Form Inputs
    setTitle("");
    setContent("");
  };

  // Handle Updating Note
  const handleUpdateNote = (event: React.FormEvent) => {
    event.preventDefault();

    if (!selectedNote) {
      return;
    }

    const updatedNote: Note = {
      id: selectedNote.id,
      title: title,
      content: content,
    };

    const updatedNotesList = notes.map((note) =>
      note.id === selectedNote.id ? updatedNote : note
    );

    // Update states
    setNotes(updatedNotesList);
    setTitle("");
    setContent("");
    setSelectedNote(null);
  };

  // Handling user canceling the update (reset the form)
  const handleCancel = () => {
    setTitle("");
    setContent("");
    setSelectedNote(null);
  };

  // Delete Notes

  const deleteNote = (event: React.MouseEvent, noteId: number) => {
    event.stopPropagation();
    // Filter selected note
    const updatedNotes = notes.filter((note) => note.id !== noteId);

    setNotes(updatedNotes);
  };

  return (
    <div className="app-container">
      {/* Form */}
      <form
        className="note-form"
        onSubmit={(event) =>
          selectedNote ? handleUpdateNote(event) : handleAddNote(event)
        }
      >
        {/* Title Input Field */}
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        ></input>

        {/* Content Input Field */}
        <textarea
          value={content}
          onChange={(event) => setContent(event.target.value)}
          placeholder="Content"
          rows={10}
          required
        />

        {/* Display Button based on whether a note is selected for editing */}
        {selectedNote ? (
          // Button - Edit
          <div className="edit-buttons">
            <button type="submit">Save</button>
            <button onClick={handleCancel}>Cancel</button>
          </div>
        ) : (
          // Button - Add
          <button type="submit">Add Note</button>
        )}
      </form>

      {/* Notes Grid*/}
      <div className="notes-grid">
        {notes.map((note) => (
          <div
            key={note.id}
            className="note-item"
            onClick={() => handleNoteClick(note)}
          >
            <div className="notes-header">
              {/* Detele note button */}
              <button>X</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
