import "./App.css";
import { useState } from "react";

type Note = {
  id: number;
  title: string;
  content: string;
};

const App = () => {
  const [notes, setNotes] = useState<Note[]>([

    // Create Dummy Notes

    {
      id: 1,
      title: "Test note 1",
      content: "note content placeholder",
    },
    {
      id: 2,
      title: "Test note 2 ",
      content: "note content placeholder",
    },
    {
      id: 3,
      title: "Test note 3",
      content: "note content placeholder",
    },
    {
      id: 4,
      title: "Test note 4 ",
      content: "note content placeholder",
    },
    {
      id: 5,
      title: "Test note 5",
      content: "note content placeholder",
    },
    {
      id: 6,
      title: "Test note 6",
      content: "note content placeholder",
    },
    {
      id: 7,
      title: "Test note 7",
      content: "note content placeholder",
    },
    {
      id: 8,
      title: "Test note 8",
      content: "note content placeholder",
    },
  ]);

  // State for Form Inputs

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  // Handle Form Submission

  const handleSubmit = (event: React.FormEvent) => {
    // prevent form from submitting and refreshing page
    event.preventDefault();
    console.log("title: ", title);
    console.log("content: ", content);
  };

  // Create a New Note Object

  const newNote: Note = {
    id: notes.length + 1,
    title: title,
    content: content,
  };

  // Update State with New Note
  setNotes([newNote, ...notes]);

  // Reset Form Inputs
  setTitle("");
  setContent("");

  return (
    <div className="app-container">
      {/* Form */}
      <form className="note-form" onSubmit={(event) => handleSubmit(event)}>
        {/* Title Input Field */}
        <input
          value={title}
          onChange={(event) => setTitle(event.target.value)}
          placeholder="Title"
          required
        ></input>

        {/* Content Input Field */}
        <textarea placeholder="Content" rows={10} required />
        <button type="submit">Add Note</button>
      </form>

      {/* Notes Grid*/}
      <div className="notes-grid">
        {notes.map((note) => (
          <div className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
          </div>
        ))}
      </div>

    </div>
  );
};

export default App;
